import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { postAPI, commentAPI } from '../services/api';
import './PostDetail.css';

function PostDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(true);

  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isAuthor = user.id === post?.authorId;

  useEffect(() => {
    loadPost();
  }, [id]);

  const loadPost = async () => {
    try {
      const response = await postAPI.getById(id);
      setPost(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Yazƒ± y√ºklenirken hata:', error);
      setLoading(false);
    }
  };

  const handleLike = async () => {
    try {
      await postAPI.like(id);
      loadPost(); // Yenile
    } catch (error) {
      console.error('Beƒüeni hatasƒ±:', error);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!comment.trim()) return;

    try {
      await commentAPI.create({
        content: comment,
        postId: parseInt(id),
      });
      setComment('');
      loadPost(); // Yorumlarƒ± yenile
    } catch (error) {
      console.error('Yorum eklenirken hata:', error);
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Yorumu silmek istediƒüinize emin misiniz?')) return;

    try {
      await commentAPI.delete(commentId);
      loadPost();
    } catch (error) {
      console.error('Yorum silinirken hata:', error);
    }
  };

  const handleDeletePost = async () => {
    if (!window.confirm('Yazƒ±yƒ± silmek istediƒüinize emin misiniz?')) return;

    try {
      await postAPI.delete(id);
      navigate('/');
    } catch (error) {
      console.error('Yazƒ± silinirken hata:', error);
    }
  };

  if (loading) {
    return <div className="loading">Y√ºkleniyor...</div>;
  }

  if (!post) {
    return <div className="error">Yazƒ± bulunamadƒ±.</div>;
  }

  return (
    <div className="post-detail-container">
      <button onClick={() => navigate('/')} className="btn-back">‚Üê Geri</button>

      <article className="post-detail">
        {post.imageUrl && (
          <img src={post.imageUrl} alt={post.title} className="post-image-large" />
        )}

        <h1>{post.title}</h1>

        <div className="post-info">
          <span>üë§ {post.author?.name}</span>
          <span>üìÅ {post.category?.name}</span>
          <span>üìÖ {new Date(post.createdAt).toLocaleDateString('tr-TR')}</span>
        </div>

        <div className="post-actions">
          <button onClick={handleLike} className="btn-like">
            ‚ù§Ô∏è Beƒüen ({post.likes})
          </button>
          {isAuthor && (
            <>
              <button onClick={() => navigate(`/edit-post/${id}`)} className="btn-edit">
                ‚úèÔ∏è D√ºzenle
              </button>
              <button onClick={handleDeletePost} className="btn-delete">
                üóëÔ∏è Sil
              </button>
            </>
          )}
        </div>

        <div className="post-content">
          <p>{post.content}</p>
        </div>
      </article>

      {/* Yorumlar */}
      <div className="comments-section">
        <h2>Yorumlar ({post.comments?.length || 0})</h2>

        {/* Yorum Ekleme Formu */}
        {user.id && (
          <form onSubmit={handleAddComment} className="comment-form">
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Yorumunuzu yazƒ±n..."
              rows="3"
              required
            />
            <button type="submit" className="btn-primary">Yorum Yap</button>
          </form>
        )}

        {/* Yorum Listesi */}
        <div className="comments-list">
          {post.comments?.length === 0 ? (
            <p className="no-comments">Hen√ºz yorum yok.</p>
          ) : (
            post.comments?.map(comment => (
              <div key={comment.id} className="comment">
                <div className="comment-header">
                  <strong>{comment.user?.name}</strong>
                  <span className="comment-date">
                    {new Date(comment.createdAt).toLocaleDateString('tr-TR')}
                  </span>
                </div>
                <p>{comment.content}</p>
                {(user.id === comment.userId || user.role === 'teacher') && (
                  <button
                    onClick={() => handleDeleteComment(comment.id)}
                    className="btn-delete-comment"
                  >
                    Sil
                  </button>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default PostDetail;
